"use strict";

var typeDefs = "        \n\n  type Book { \n    author: String!\n    title: String!\n  }     \n  \n  type Possiblebook { \n    author: String\n    title: String\n  }     \n\n\n  type Podcast { \n    description: String!     \n    mp3: String!     \n    length: String!       \n    episode: Int! \n  }      \n\n  interface SearchInterface {\n    ID: String!  \n  }\n  \n  type Issue { \n    url: String                  \n    pages: Int        \n    publisher: String\n  }\n\n  enum Kind { \n    AUDIOBOOK\n    AUIDOBOOKREADALONG\n    NEWRELEASESRECENTARRIVALS\n    READALONG\n    TALKTO\n    TOPIC\n  }\n  \n  type SffAudioMedia implements SearchInterface {  \n    ID: String!\n    possiblebook: Possiblebook              \n    podcast: Podcast!                     \n    sffaudio_post: String!  \n    about: String\n    narrator: String\n  }\n\n  type RsdMedia implements SearchInterface {   \n    ID: String!\n    book: Book!\n    podcast: Podcast!\n    rsd_post: String!  \n    resource: String!\n  }\n    \n  type PdfMedia implements SearchInterface{  \n    ID: String!\n    book: Book!\n    issues: [Issue] \n  }\n \n  type ArticlePage implements SearchInterface { \n    ID: String!\n    headline: String!\n    article_post: String!\n  }     \n\n  type MentionPage implements SearchInterface{ \n    ID: String!\n    headline: String!\n    mention_post: String!  \n  }     \n\n  type Query { \n    search_site_content(search_text: String!): [SearchInterface]\n    rsd_media_content(get_rsd: String):[RsdMedia] \n    pdf_media_content(get_pdf:String): [PdfMedia]\n    sffaudio_media_content(get_sffaudio:String): [SffAudioMedia]\n    page_content(get_page:String):[ArticlePage]\n  }\n\n\n\n\n  schema {\n    query: Query\n  }\n";

module.exports.typeDefs = typeDefs;